import axios from 'axios';

const apiUrl = "https://localhost:5002"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/`)
    return result.data;
  },

  addTask: async (name, iscomplete) => {
    try {
      const result = await axios.post(`${apiUrl}/`, { name, iscomplete });
      return result.data;
    } catch (error) {
      console.error("Error adding task:", error);
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      const result = await axios.put(`${apiUrl}/${id}`, { isComplete });
      return result.data;
    } catch (error) {
      console.error("Error updating task ", error.message);
    }
  },

  deleteTask: async (id) => {
const result=await axios.delete(`${apiUrl}/${id}`)
return result.data;
}
};
// בקובץ service.js תמצאי את הפונקציות שמבצעות את הקריאות ל-API.
